import React from 'react';

const Article = ({urlImage, volanta, bajada, title, author, imageAuthor, marquesina, url}) => {
  return (
    <article className='article'>
      {urlImage ? (
        <div className='article-image'>
          <img src={urlImage} alt={title} title={title} />
        </div>
      ) : null}
      <div className='article-description'>
        <h2 className='article-description-title'>
          <em className='article-description-volanta'>{volanta}.</em> {title}
        </h2>
        {bajada ? (
          <p className='article-description-bajada'>
            {bajada}
          </p>
        ) : null}
        {marquesina ? (
          <strong title={marquesina} className='article-description-marquesina'>
            {marquesina}
          </strong>
        ) : null}
        {imageAuthor ? (
        <div className='article-image'>
          <img src={imageAuthor} alt={author} title={author} />
        </div>
      ) : null}
        <strong className='article-description-author'>
          <a href='#' title={author}>
            {author}
          </a>
        </strong>
      </div>
    </article>
  );
};

Article.defaultProps = {
  urlImage: 'There is not image',
  volanta: 'There is not volanta',
  title: 'There is not title',
  author: '',
};

export default Article;
