import React from 'react';

const Article = ({urlImage, volanta, bajada, title, author, imageAuthor, marquesina, url}) => {
  return (
    <article className={imageAuthor ? 'articleAuthor' : 'article'}>
      {urlImage ? (
        <div className='article-image'>
          <img src={urlImage} alt={title} title={title} />
        </div>
      ) : null}
      <div className={imageAuthor ? 'article-descriptionAuthor' : 'article-description'}>
        {title ? (
          <h2 className='article-description-title'>
            {volanta ? (<em className='article-description-volanta'>{volanta}.</em> ) : null} {title}
          </h2>
        ) : null}
        {bajada ? (
          <p className='article-description-bajada'>
            {bajada}
          </p>
        ) : null}
        {marquesina ? (
          <strong title={marquesina} className='article-description-marquesina'>
            {marquesina}
          </strong>
        ) : null}
        {imageAuthor ? (
          <div className='article-description-contentImageAuthor'>
            <img className='article-description-imageAuthor' src={imageAuthor} alt={author} title={author} />
          </div>
        ) : null}
        {author ? (
          <strong className='article-description-author'>
            <a href='#' title={author}>
              {author}
            </a>
          </strong>
        ) : null}
      </div>
    </article>
  );
};

Article.defaultProps = {
  urlImage: 'There is not image',
  volanta: 'There is not volanta',
  title: 'There is not title',
  author: '',
};

export default Article;
